class RefImpl {
    constructor(value) {
        this.__v_isRef = true;
        this._rawValue = value;
        // value -> reactive 包裹之后的结果
        // 1. 看看 value 是不是对象
        this._value = convert(value);
        this.dep = new Set();
    }
    get value() {
        trackRefValue(this);
        return this._value;
    }
    set value(newValue) {
        if (hasChanged(newValue, this._rawValue)) {
            // 一定要先去修改了 value 值
            this._rawValue = newValue;
            this._value = convert(newValue);
            // 触发依赖
            triggerEffects(this.dep);
        }
    }
}
